mapper.insert:
    	insert into role (
			    role_name,
			    role_desc,
         		) 
          value (
			    #{role_name},
			    #{role_desc}
          )

mapper.update:
update role set
	role_name = #{role_name},
	role_desc = #{role_desc}
where role_id = #{role_id}

mapper.insert:
    	insert into url_path_role (
			    role_id,
			    url_path_id,
         		) 
          value (
			    #{role_id},
			    #{url_path_id}
          )

mapper.update:
update url_path_role set
	role_id = #{role_id},
	url_path_id = #{url_path_id}
where role_id = #{role_id}
  and url_path_id = #{url_path_id}

mapper.delete:
    	delete from url_path_role 
    	where role_id = #{role_id}
		  and url_path_id = #{url_path_id};
